/*	Countries - PostgreSQL
	================================================
	Table of Countries for Customers
	================================================ */

	DROP TABLE IF EXISTS countries;

	CREATE TABLE countries (
		id CHAR(2) PRIMARY KEY,
		name VARCHAR(64) NOT NULL,
		fullname VARCHAR(160),
		localname VARCHAR(48),
		capital VARCHAR(24),
		continent VARCHAR(16),
		alpha3 CHAR(3),
		numeric3 CHAR(3),
		tld CHAR(2),
		tld_notes VARCHAR(64),
		phone_prefix VARCHAR(48),
		tz VARCHAR(24),
		dst VARCHAR(24),
		area INT,
		population INT,
		currency CHAR(3)
	);

;
